import { expect, test } from "@jest/globals";
import * as version from "../lib/version";

/*
 * versions for tests
 */
const testVersions = {
  "1.0.1": {
    major: 1,
    minor: 0,
    patch: 1,
    suffix: "",
    original: "1.0.1",
    parsed: true,
  },
  "2.0": {
    major: 2,
    minor: 0,
    patch: 0,
    suffix: "",
    original: "2.0",
    parsed: true,
  },
  "3": {
    major: 3,
    minor: 0,
    patch: 0,
    suffix: "",
    original: "3",
    parsed: true,
  },
  "4.abra": {
    major: 4,
    minor: 0,
    patch: 0,
    suffix: "abra",
    original: "4.abra",
    parsed: false,
  },
  "4.2.abra": {
    major: 4,
    minor: 2,
    patch: 0,
    suffix: "abra",
    original: "4.2.abra",
    parsed: true,
  },
  "4.3.abra.kadabra": {
    major: 4,
    minor: 3,
    patch: 0,
    suffix: "abra.kadabra",
    original: "4.3.abra.kadabra",
    parsed: true,
  },
  "4.4.abra-kadabra": {
    major: 4,
    minor: 4,
    patch: 0,
    suffix: "abra-kadabra",
    original: "4.4.abra-kadabra",
    parsed: true,
  },
  "5.1.2-custom": {
    major: 5,
    minor: 1,
    patch: 2,
    suffix: "custom",
    original: "5.1.2-custom",
    parsed: true,
  },
  "5.1.3-alpha.2": {
    major: 5,
    minor: 1,
    patch: 3,
    suffix: "alpha.2",
    original: "5.1.3-alpha.2",
    parsed: true,
  },
  "1.0.1.2.3.4": {
    major: 1,
    minor: 0,
    patch: 1,
    suffix: "2.3.4",
    original: "1.0.1.2.3.4",
    parsed: true,
  },
  "1.0.1.beta": {
    major: 1,
    minor: 0,
    patch: 1,
    suffix: "beta",
    original: "1.0.1.beta",
    parsed: true,
  },
} as { [key: string]: version.versionInfo };

const parseTests = Object.keys(testVersions);

parseTests.forEach((versionName) =>
  test(`Parse '${versionName}'`, async () => {
    const v: version.versionInfo = version.parse(versionName);
    expect(v).toEqual(testVersions[versionName]);
  })
);

const compareTests = [
  {
    name: "same version",
    a: testVersions["1.0.1"],
    b: testVersions["1.0.1"],
    expected: 0,
  },
  {
    name: "greater major",
    a: testVersions["1.0.1"],
    b: testVersions["2.0"],
    expected: -1,
  },
  {
    name: "less major",
    a: testVersions["2.0"],
    b: testVersions["1.0.1"],
    expected: 1,
  },
  {
    name: "greater suffix",
    a: testVersions["1.0.1.2.3.4"],
    b: testVersions["1.0.1.beta"],
    expected: -1,
  },
];

compareTests.forEach((_case) =>
  test(`Compare '${_case.name}'`, async () => {
    expect(version.compare(_case.a, _case.b)).toBe(_case.expected);
  })
);
